<script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/1.3.4/jspdf.min.js"></script>

<script>
    $(document).ready(function () {
        var status = false;
        var stageopdracht;

        $('#exporteerPDF').click(function () {
            var pdf = new jsPDF('p', 'pt', 'letter');
            var img = "data:image/png;base64,";
            pdf.addImage(img, 'JPEG', 15, 40, 220, 160);
            pdf.setFont("arial", "bold");
            pdf.setFontSize(30);
            pdf.text("Stageopdracht", 300, 120);
            source = $('#content')[0];
            specialElementHandlers = {
                '#bypassme': function (element, renderer) {
                    return true
                }
            };
            margins = {
                top: 200,
                bottom: 60,
                left: 40,
                width: 522
            };
            pdf.fromHTML(
                source,
                margins.left,
                margins.top, {
                    'width': margins.width,
                    'elementHandlers': specialElementHandlers
                },

                function (dispose) {
                    pdf.save('Stageopdracht_' + stageopdracht.Opdrachtgever.Bedrijfsnaam);
                }, margins
            );
        });

        $('#markeerAlsFavoriet').click(function () {
            $.ajax({
                url: BaseURL+"/stageopdrachten/updateFavorite/" + <?php echo $_POST['id_opdracht']; ?> +"/" + sessionStorage.getItem("userId"),
                type: "PUT",
                data: stageopdracht,
                async: false,
                beforeSend: function (xhr) {
                    xhr.setRequestHeader('Authorization', 'Bearer ' + sessionStorage.getItem('tokenKey'));
                },
                success: function (data) {
                    if (status) {
                        $('#markeerAlsFavoriet').html("Vind ik leuk");
                        status = false;
                    } else {
                        $('#markeerAlsFavoriet').html("Vind ik niet meer leuk");
                        status = true;
                    }
                },
                error: function (data, jqXHR, textStatus, errorThrown) {
                    console.error("Error: Status: " + textStatus + " Message: " + errorThrown + data);
                }
            });
        });

        $('#selecteerStagevoorstel').click(function () {
            var stage = {
                StartDatum: "01/01/2000", EindDatum: "01/01/2000", StageStatus: "InBehandeling",
                StudentId: sessionStorage.getItem('userId'), StageOpdrachtId: stageopdracht.Id
            };
            $.ajax({
                url: BaseURL+"/stage",
                type: "POST",
                data: stage,
                async: false,
                beforeSend: function (xhr) {
                    xhr.setRequestHeader('Authorization', 'Bearer ' + sessionStorage.getItem('tokenKey'));
                },
                success: function () {
                    $('#success').show();
                },
                error: function (data, jqXHR, textStatus, errorThrown) {
                    $('#fail').show();
                }
            });
        });

        $.ajax({
            url: BaseURL+"/stageopdrachten/favorites/" + <?php echo $_POST['id_opdracht']; ?>,
            type: "Get",
            dataType: "json",
            async: false,
            beforeSend: function (xhr) {
                xhr.setRequestHeader('Authorization', 'Bearer ' + sessionStorage.getItem('tokenKey'));
            },
            success: function (data) {
                stageopdracht = data;
                //Bedrijf
                $('#titel').html(data.Opdrachtgever.Bedrijfsnaam + " : " + data.Omgeving);
                $('#time').html("Insgestuurd op: " + data.TimeStamp);

                //Bedrijf
                $('#bedrijfsnaam').html(data.Opdrachtgever.Bedrijfsnaam);
                $('#adres').html(data.Opdrachtgever.Adres + " " + data.Opdrachtgever.Huisnummer);
                $('#gemeente').html(data.Opdrachtgever.Postcode + " " + data.Opdrachtgever.Gemeente);
                $('#telefoonnummer').html(data.Opdrachtgever.Telefoonnummer);

                //Technische informatie
                $('#locatie').html(data.Locatie);
                $('#aantalGewensteStagiairs').html(data.AantalGewensteStagiairs);
                $('#omgeving').html(data.Omgeving);
                $('#afstudeerrichting').html(getAfstudeerRichting(data.VoorkeurAfstudeerrichting));
                $('#periode').html(getPeriode(data.Periode));

                //Stageopdracht
                $('#omschrijving').html(data.Omschrijving);
                $('#verwachtingen').html(getInleidendeActiviteit(data.InleidendeActiviteiten));
                $('#randvoorwaarden').html(data.Randvoorwaarden);
                $('#onderzoeksthema').html(data.Onderzoeksthema);
                $('#beschrijvingTechnischeOmgeving').html(data.BeschrijvingTechnischeOmgeving);

                for (var i = 0; i < data.StudentFavorieten.length; i++) {
                    if (data.StudentFavorieten[i].Id === 1) {
                        $('#markeerAlsFavoriet').html('Vind ik niet meer leuk');
                        status = true;
                        break;
                    }
                }
            },
            error: function (data, jqXHR, textStatus, errorThrown) {
                console.error("Error: Status: " + textStatus + " Message: " + errorThrown + data);
            }
        });
    });
</script>
